import Layout from '../components/MyLayout';
import Link from 'next/link';

function getPost() {
    return [
        { id: 'hello-nextjs', title: 'Hello Next.js' },
        { id: 'learn-next.js', title: 'Learn next.js is awsome' },
        { id: 'deploy-nextjs', title: 'Deploy apps with ZEIT' }
    ];
}

export default function Blog(){
    return (
        <Layout>
            <h1>My Blog</h1>
            <ul>
                {getPost().map(post => (
                    <li key={post.id}>
                        <Link href="/p/[id]" as={`/p/${post.id}`}>
                            <a>{post.title}</a>
                        </Link>
                    </li>
                ))}
            </ul>
            <style jsx>{`
                h1,
                a {
                    font-family: 'Arial';
                }

                ul {
                    padding: 0;
                }

                li {
                    list-style: none;
                    margin: 5px 0;
                }

                a{
                    text-decoration: none;
                    color: blue;
                }

                a:hover {
                    opacity: 0.6;
                }
            `}</style>
        </Layout>
    );
}














